import React, { useState, useEffect } from 'react';
import { styled } from '@mui/material/styles';
import DashboardIcon from '@mui/icons-material/Dashboard';
import InboxIcon from '@mui/icons-material/Inbox';
import SettingsIcon from '@mui/icons-material/Settings';
import { Link } from 'react-router-dom';
import GroupAddOutlinedIcon from '@mui/icons-material/GroupAddOutlined';
import { Select, MenuItem, Typography, TextField, Drawer, List, ListItem, ListItemIcon, ListItemText, Divider, Button, Card, CardContent, Grid, FormControl, InputLabel } from '@mui/material';
import { Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Paper } from '@mui/material';
import SendIcon from '@mui/icons-material/Send';
import CloseIcon from '@mui/icons-material/Close';
import emailjs from 'emailjs-com';

const StyledDrawer = styled(Drawer)(({ theme }) => ({
  width: 240,
  flexShrink: 0,
  '& .MuiDrawer-paper': {
    width: 240,
    backgroundColor: '#1e1e1e',
    color: '#fff',
    margin: '10px',
    borderRadius: '20px',
    padding: '10px',
  },
}));

const StyledListItemText = styled(ListItemText)(({ theme }) => ({
  color: '#fff',
}));

const StyledDivider = styled(Divider)(({ theme }) => ({
  backgroundColor: '#4d4d4d',
}));

const SidebarMenu = () => {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [userType, setUserType] = useState('');
  const [selectedSection, setSelectedSection] = useState('dashboard');
  const [notifications, setNotifications] = useState([]);
  const [selectedStatus, setSelectedStatuses] = useState({});

  // NEW USER STATES
  const [newname, setnewName] = useState('');
  const [newemail, setnewEmail] = useState('');
  const [newpassword, setnewPassword] = useState('');
  const [newtype, setnewType] = useState('staff');

  useEffect(() => {
    handleGetNotifications();
    // Fetch current user data
    const userTypeFromStorage = localStorage.getItem('userType');
    const nameFromStorage = localStorage.getItem('name');
    const emailFromStorage = localStorage.getItem('email');
    if (userTypeFromStorage) setUserType(userTypeFromStorage);
    if (nameFromStorage) setName(nameFromStorage);
    if (emailFromStorage) setEmail(emailFromStorage);
  }, []);

  const handleGetNotifications = async () => {
    try {
      const response = await fetch('http://localhost:3001/getnotifications');
      if (response.ok) {
        const data = await response.json();
        setNotifications(data);
        setInitialSelectedStatuses(data);
      } else {
        console.error('Failed to fetch notifications:', response.statusText);
      }
    } catch (error) {
      console.error('Error fetching notifications:', error);
    }
  }

  const setInitialSelectedStatuses = (notifications) => {
    const initialStatuses = {};
    notifications.forEach(notification => {
      initialStatuses[notification.id] = notification.tick;
    });
    setSelectedStatuses(initialStatuses);
  };

  const renderRowColor = (index) => {
    return index % 2 === 0 ? 'white' : '#f2f2f2';
  };

  const handleChange = async (event, notification) => {
    const newStatus = event.target.value;
    setSelectedStatuses(prevState => ({
      ...prevState,
      [notification.id]: newStatus
    }));
    try {
      const response = await fetch(`http://localhost:3001/updatenotification/${notification.id}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ tick: newStatus }),
      });
      if (!response.ok) {
        throw new Error('Failed to update notification status');
      }
      if (newStatus === 'a') {
        sendEmails(notification.title, notification.body); // Send email when approved
      }
    } catch (error) {
      console.error('Error updating notification status:', error);
    }
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      const response = await fetch('http://localhost:3001/createuser', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          name: newname,
          email: newemail,
          password: newpassword,
          type: newtype,
        }),
      });

      if (response.ok) {
        const data = await response.json();
        console.log('User created successfully:', data);
      } else {
        console.error('Failed to create user:', response.statusText);
      }
    } catch (error) {
      console.error('Error creating user:', error);
    }
  };

  const sendEmails = (title, body) => {
    const recipientEmails = ['labhchandrabothra@gmail.com', 'labhbothra0@gmail.com']; // List of recipient email addresses
    recipientEmails.forEach(recipientEmail => {
      emailjs.send('service_7y6eu4l', 'template_hy4zaa6', {
        to_name: recipientEmail,
        title: title,
        body: body,
      }, '3XyzWvUrjvRULOnrF')
        .then((response) => {
          console.log('Email sent successfully to', recipientEmail, ':', response);
        })
        .catch((error) => {
          console.error('Error sending email to', recipientEmail, ':', error);
        });
    });
  };

  return (
    <div style={{ display: 'flex' }}>
      <StyledDrawer variant="permanent" anchor="left">
        <List>
          {/* Dashboard Section */}
          <ListItem
            button
            component={Link}
            onClick={() => setSelectedSection('dashboard')}
          >
            <ListItemIcon>
              <DashboardIcon style={{ color: '#fff' }} />
            </ListItemIcon>
            <StyledListItemText primary="Dashboard" />
          </ListItem>

          {/* Inbox Section */}
          <StyledDivider />
          <ListItem
            button
            component={Link}
            onClick={() => setSelectedSection('inbox')}
          >
            <ListItemIcon>
              <InboxIcon style={{ color: '#fff' }} />
            </ListItemIcon>
            <StyledListItemText primary="Inbox" />
          </ListItem>

          <ListItem
            button
            component={Link}
            onClick={() => setSelectedSection('add')}
          >
            <ListItemIcon>
              <GroupAddOutlinedIcon style={{ color: '#fff' }} />
            </ListItemIcon>
            <StyledListItemText primary="Add Staff" />
          </ListItem>

          {/* Settings Section */}
          <StyledDivider />
          <ListItem
            button
            component={Link}
            onClick={() => setSelectedSection('settings')}
          >
            <ListItemIcon>
              <SettingsIcon style={{ color: '#fff' }} />
            </ListItemIcon>
            <StyledListItemText primary="Settings" />
          </ListItem>
        </List>
      </StyledDrawer>

      {/* Content Area */}
      <div style={{ flex: 1, marginLeft: '150px', marginTop: '50px', marginRight: '20px' }}>
        {selectedSection === 'dashboard' && (
          <div>
            {/* Dashboard Content */}
            <h2>👋 Welcome {name}</h2>
            <p style={{ color: 'grey', fontSize: '20px' }}>Overview</p>
            <Grid container spacing={2} >
              <Grid item xs={12} sm={3} >
                <Card variant="outlined" sx={{ backgroundColor: '#2196F3' }} style={{ borderRadius: '12px' }}>
                  <CardContent>
                    <Typography variant="h5" color="white" gutterBottom>
                      Total Notifications
                    </Typography>
                    <Typography variant="h4" color="white">
                      {notifications.length}
                    </Typography>
                  </CardContent>
                </Card>
              </Grid>
              {/* Other cards */}
            </Grid>
          </div>
        )}

        {selectedSection === 'inbox' && (
          <div>
            <div>
              <h2>Inbox</h2>
              <TableContainer component={Paper}>
                <Table>
                  <TableHead>
                    <TableRow style={{ backgroundColor: 'black', color: 'white' }}>
                      <TableCell style={{ color: 'white' }}>Status</TableCell>
                      <TableCell style={{ color: 'white' }}>Title</TableCell>
                      <TableCell style={{ color: 'white' }}>Name</TableCell>
                      <TableCell style={{ color: 'white' }}>Message</TableCell>
                      <TableCell style={{ color: 'white' }}>ID</TableCell>
                    </TableRow>
                  </TableHead>
                  <TableBody>
                    {notifications.map((notification, index) => (
                      <TableRow key={notification.id} style={{ backgroundColor: renderRowColor(index) }}>
                        <TableCell>
                          <Select
                            value={selectedStatus[notification.id] || ''}
                            onChange={(event) => handleChange(event, notification)}
                            style={{ color: selectedStatus[notification.id] === 'p' ? 'grey' : selectedStatus[notification.id] === 'r' ? 'red' : 'green' }}
                          >
                            <MenuItem value="p">Pending</MenuItem>
                            <MenuItem value="a">Approve</MenuItem>
                            <MenuItem value="r">Reject</MenuItem>
                          </Select>
                        </TableCell>
                        <TableCell>{notification.title}</TableCell>
                        <TableCell>{notification.name}</TableCell>
                        <TableCell>{notification.message}</TableCell>
                        <TableCell>{notification.id}</TableCell>
                      </TableRow>
                    ))}
                  </TableBody>
                </Table>
              </TableContainer>
            </div>
          </div>
        )}

        {selectedSection === 'settings' && (
          <div>
            {/* Settings Content */}
            <h2>Settings</h2>
            <p>This is the settings content.</p>
          </div>
        )}

        {selectedSection === 'add' && (
          <div>
            {/* Settings Content */}
            <h2>User manager</h2>
            <form>
              <TextField
                label="Name"
                value={newname}
                onChange={(e) => setnewName(e.target.value)}
                fullWidth
                required
                style={{ marginBottom: '10px' }}
              />
              {/* Other input fields */}
              <Button variant="contained" type="submit" onClick={handleSubmit} style={{ backgroundColor: 'black', color: 'white' }}>
                Submit
              </Button>
            </form>
            <br />
            <div>
              <h2>Existing Users</h2>
              <TableContainer component={Paper}>
                <Table>
                  <TableHead>
                    <TableRow>
                      <TableCell>Name</TableCell>
                      <TableCell>Email</TableCell>
                      <TableCell>Type</TableCell>
                    </TableRow>
                  </TableHead>
                  <TableBody>
                    {users.map((user) => (
                      <TableRow key={user.id}>
                        <TableCell>{user.name}</TableCell>
                        <TableCell>{user.email}</TableCell>
                        <TableCell>{user.type}</TableCell>
                      </TableRow>
                    ))}
                  </TableBody>
                </Table>
              </TableContainer>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

export default SidebarMenu;
