import React, { useState, useEffect } from 'react';
import { styled } from '@mui/material/styles';
import { Drawer, List, ListItem, ListItemIcon, ListItemText, Divider, Button, TextField } from '@mui/material';
import DashboardIcon from '@mui/icons-material/Dashboard';
import SettingsIcon from '@mui/icons-material/Settings';
import { Link } from 'react-router-dom';

const StyledDrawer = styled(Drawer)(({ theme }) => ({
  width: 240,
  flexShrink: 0,
  '& .MuiDrawer-paper': {
    width: 240,
    backgroundColor: '#1e1e1e',
    color: '#fff',
    margin: '10px',
    borderRadius: '20px',
    padding: '10px',
  },
}));

const StyledListItemText = styled(ListItemText)(({ theme }) => ({
  color: '#fff',
}));

const StyledDivider = styled(Divider)(({ theme }) => ({
  backgroundColor: '#4d4d4d',
}));

function Parents() {
  const [email, setEmail] = useState('');
  const [emails, setEmails] = useState([]);
  const [selectedSection, setSelectedSection] = useState('dashboard');


  useEffect(() => {
    fetchEmails();
  }, []);

  const fetchEmails = () => {
    fetch('./parents.json') // Adjust the path to parents.json
      .then(response => response.json())
      .then(data => setEmails(data.parents))
      .catch(error => console.error('Error fetching emails:', error));
  };

  const handleAddEmail = () => {
    if (email.trim() !== '') {
      const updatedEmails = [...emails, email];
      setEmails(updatedEmails);
      setEmail('');

      // Update JSON file
      fetch('./parents.json', { // Adjust the path to the server-side endpoint
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ parents: updatedEmails }),
      })
      .then(() => {
        // Successfully updated JSON file, optionally fetch emails again
        // fetchEmails();
      })
      .catch(error => console.error('Error updating emails:', error));
    }
  };

  return (
    <div style={{ display: 'flex' }}>
      <StyledDrawer variant="permanent" anchor="left">
        <List>
          {/* Dashboard Section */}
          <ListItem
            button
            component={Link}
            onClick={() => setSelectedSection('dashboard')}
          >
            <ListItemIcon>
              <DashboardIcon style={{ color: '#fff' }} />
            </ListItemIcon>
            <StyledListItemText primary="Dashboard" />
          </ListItem>

          {/* Inbox Section */}
          <StyledDivider />

          {/* Settings Section */}
          <StyledDivider />
          <ListItem
            button
            component={Link}
            onClick={() => setSelectedSection('settings')}
          >
            <ListItemIcon>
              <SettingsIcon style={{ color: '#fff' }} />
            </ListItemIcon>
            <StyledListItemText primary="Settings" />
          </ListItem>
        </List>
      </StyledDrawer>

      {/* Content Area */}
      <div style={{ flex: 1, marginLeft: '150px', marginTop: '50px', marginRight: '20px' }}>
        {/* Email Input */}
        <TextField
          label="Add Email"
          variant="outlined"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />
        <Button variant="contained" onClick={handleAddEmail}>Add</Button>

        {/* List of Emails */}
        <List>
          {emails.map((parents, index) => (
            <ListItem key={index}>
              <ListItemText primary={parents} />
            </ListItem>
          ))}
        </List>
      </div>
    </div>
  );
}

export default Parents;
