import React, { useState, useEffect } from 'react';
import { styled } from '@mui/material/styles';
import { Drawer, List, ListItem, ListItemIcon, ListItemText, Divider, Button, TextField } from '@mui/material';
import DashboardIcon from '@mui/icons-material/Dashboard';
import SettingsIcon from '@mui/icons-material/Settings';
import { Link } from 'react-router-dom';

const StyledDrawer = styled(Drawer)(({ theme }) => ({
  width: 240,
  flexShrink: 0,
  '& .MuiDrawer-paper': {
    width: 240,
    backgroundColor: '#1e1e1e',
    color: '#fff',
    margin: '10px',
    borderRadius: '20px',
    padding: '10px',
  },
}));

const StyledListItemText = styled(ListItemText)(({ theme }) => ({
  color: '#fff',
}));

const StyledDivider = styled(Divider)(({ theme }) => ({
  backgroundColor: '#4d4d4d',
}));

function Parents() {
    const [selectedSection, setSelectedSection] = useState('dashboard');
    const [notifications, setNotifications] = useState([]);
    const [selectedStatus, setSelectedStatuses] = useState({});
    const [newemail, setnewEmail] = useState('');

    const handleSubmit = async (event) => {
        event.preventDefault();
        try {
          const response = await fetch('http://localhost:3001/addparentemail', {
            method: 'POST',
            headers: {
              'Content-Type': 'application/json',
            },
            body: JSON.stringify({
              email: newemail,
            }),
          });

          if (response.ok) {
            const data = await response.json();
            console.log('Parents Email Added successfully:', data);
          } else {
            console.error('Failed to add email:', response.statusText);
          }
        } catch (error) {
          console.error('Error creating email:', error);
        }
      };

  return (
    <div style={{ display: 'flex' }}>
      <StyledDrawer variant="permanent" anchor="left">
        <List>
          {/* Dashboard Section */}
          <ListItem
            button
            component={Link}
            onClick={() => setSelectedSection('dashboard')}
          >
            <ListItemIcon>
              <DashboardIcon style={{ color: '#fff' }} />
            </ListItemIcon>
            <StyledListItemText primary="Dashboard" />
          </ListItem>

          {/* Inbox Section */}
          <StyledDivider />

          {/* Settings Section */}
          <StyledDivider />
          <ListItem
            button
            component={Link}
            onClick={() => setSelectedSection('settings')}
          >
            <ListItemIcon>
              <SettingsIcon style={{ color: '#fff' }} />
            </ListItemIcon>
            <StyledListItemText primary="Settings" />
          </ListItem>
        </List>
      </StyledDrawer>

      {/* Content Area */}
      <div style={{ flex: 1, marginLeft: '150px', marginTop: '50px', marginRight: '20px' }}>
        {/* Email Input */}
        <TextField
          label="Add Email"
          variant="outlined"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />
        <Button variant="contained" onClick={handleAddEmail}>Add</Button>

        {/* List of Emails */}
