import React, { useState, useEffect } from 'react';
import { styled } from '@mui/material/styles';
import { Typography, TextField, Drawer, List, ListItem, ListItemIcon, ListItemText, Divider, Button, IconButton, Card, CardContent, Grid } from '@mui/material';
import DashboardIcon from '@mui/icons-material/Dashboard';
import SettingsIcon from '@mui/icons-material/Settings';
import SendIcon from '@mui/icons-material/Send';
import CloseIcon from '@mui/icons-material/Close';
import { Link } from 'react-router-dom';
import { Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Paper } from '@mui/material';
import NotificationStateCircle from './NotificationStateCircle';

const StyledDrawer = styled(Drawer)(({ theme }) => ({
  width: 240,
  flexShrink: 0,
  '& .MuiDrawer-paper': {
    width: 240,
    backgroundColor: '#1e1e1e',
    color: '#fff',
    margin: '10px',
    borderRadius: '20px',
    padding: '10px',
  },
}));

const StyledListItemText = styled(ListItemText)(({ theme }) => ({
  color: '#fff',
}));

const StyledDivider = styled(Divider)(({ theme }) => ({
  backgroundColor: '#4d4d4d',
}));

function Parents() {
  const [name, setName] = useState('');
  const [selectedSection, setSelectedSection] = useState('dashboard');
  const [parentEmails, setParentEmails] = useState([]);

  // Fetch parent emails from JSON file on component mount
  useEffect(() => {
    fetchParentEmails();
  }, []);

  // Function to fetch parent emails from JSON file
  const fetchParentEmails = async () => {
    try {
      const response = await fetch('parentEmails.json');
      const data = await response.json();
      setParentEmails(data.emails);
    } catch (error) {
      console.error('Error fetching parent emails:', error);
    }
  };

  // Function to handle adding a parent email
  const addParentEmail = async () => {
    try {
      const newEmail = { email: name };
      await fetch('parentEmails.json', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(newEmail),
      });
      // Update state with the new email
      setParentEmails([...parentEmails, newEmail]);
      setName(''); // Clear input field
    } catch (error) {
      console.error('Error adding parent email:', error);
    }
  };

  // Function to handle deleting a parent email
  const deleteParentEmail = async (email) => {
    try {
      await fetch('parentEmails.json', {
        method: 'DELETE',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ email }),
      });
      // Update state by removing the deleted email
      setParentEmails(parentEmails.filter((item) => item.email !== email));
    } catch (error) {
      console.error('Error deleting parent email:', error);
    }
  };

  return (
    <div style={{ display: 'flex' }}>
      <StyledDrawer variant="permanent" anchor="left">
        <List>
          {/* Dashboard Section */}
          <ListItem button component={Link} onClick={() => setSelectedSection('dashboard')}>
            <ListItemIcon>
              <DashboardIcon style={{ color: '#fff' }} />
            </ListItemIcon>
            <StyledListItemText primary="Dashboard" />
          </ListItem>

          {/* Inbox Section */}
          <StyledDivider />

          {/* Settings Section */}
          <StyledDivider />
          <ListItem button component={Link} onClick={() => setSelectedSection('settings')}>
            <ListItemIcon>
              <SettingsIcon style={{ color: '#fff' }} />
            </ListItemIcon>
            <StyledListItemText primary="Settings" />
          </ListItem>
        </List>
      </StyledDrawer>

      {/* Content Area */}
      <div style={{ flex: 1, marginLeft: '150px', marginTop: '50px', marginRight: '20px' }}>
        {selectedSection === 'dashboard' && (
          <div>
            {/* Dashboard Content */}
            <h2>👋 Welcome {name}</h2>
            <p style={{ color: 'grey', fontSize: '20px' }}>Overview</p>
          </div>
        )}

        {selectedSection === 'settings' && (
          <div>
            {/* Settings Content */}
            <h2>Settings</h2>
            <TextField
              label="Parent's Email"
              variant="outlined"
              value={name}
              onChange={(e) => setName(e.target.value)}
            />
            <Button variant="contained" color="primary" startIcon={<SendIcon />} onClick={addParentEmail}>
              Add
            </Button>
            <List>
              {parentEmails.map((email) => (
                <ListItem key={email} button>
                  <ListItemText primary={email} />
                  <IconButton onClick={() => deleteParentEmail(email)} aria-label="delete">
                    <CloseIcon />
                  </IconButton>
                </ListItem>
              ))}
            </List>
          </div>
        )}
      </div>
    </div>
  );
}

export default Parents;
